import React, { Component } from 'react'
import { Button, FormGroup, FormLabel, FormControl} from "react-bootstrap"
import { login } from './UserFunctions'
import "./Login.css"

class LoginForm extends Component {
    constructor() {
        super();
        this.state = {
            username: '',
            masterkey :'',
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    } 

    onChange(e){
        this.setState({[e.target.name]: e.target.value})
    }

    validateForm() {
        return this.state.username.length > 0 && this.state.masterkey.length > 0;
    }

    onSubmit(e) {
        e.preventDefault();
        const user = {
            username: this.state.username,
            masterkey: this.state.masterkey
        }
        login(user).then(res => {
            console.log(user, res)
            if(!res.error) {
                console.log("login success")
                this.props.hisotry.push('/programs')
            }        
        })
    }
    render(){
        return(
            <div className="Login">
                <form noValidate onSubmit={this.onSubmit}>
                    <FormGroup controlId="username" bsSize="large">
                        <FormLabel>Username</FormLabel>
                        <FormControl
                            type="username"
                            name="username"
                            value={this.state.username}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey" bsSize="large">
                        <FormLabel>Masterkey</FormLabel>
                        <FormControl
                            type="masterkey"
                            name="masterkey"
                            value={this.state.masterkey}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <div className="button-group">
                        <Button className="login-btn" block bsSize="large" disabled={!this.validateForm} type="submit">
                            Login
                        </Button>
                        or
                        <Link to="/register">
                            <Button className="register-btn" block bsSize="large">
                                Register
                            </Button>
                        </Link>
                    </div>
                </form>
            </div>
        );
    }

}
export default LoginForm
