import React, { Component } from 'react'
import { Button } from "react-bootstrap"
import { programs, remove, change } from './UserFunctions'
import { AddCredentialForm } from './AddForm'

import "./Programs.css"


class ProgramsList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            programList: [],
            makeChanges: false,
            program: '',
            username: '',
            password:'',
            password_re:'',
            programChanges: []

        }
        this.getToken = this.getToken.bind(this);
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.handleDeleteRow = this.handleDeleteRow.bind(this);
        this.handleChangeRow = this.handleChangeRow.bind(this);
    }

    removeRow(programName) {

        console.log({ programName })
        this.setState(prevState => {
            const programList = prevState.programList.filter(programList => programList.program !== programName);

            return { programList };
        });    

    }

    handleDeleteRow(programName) {
        console.log({ programName })
        remove(programName).then(res => {
            if(!res.err) {

                console.log('Credentials deleted')
                this.removeRow(programName)
                this.createTable()
            }               
        });
    }



    handleChangeRow(programID){
        const willChange = this.state.programList.find(({id}) =>  id === programID)
        this.setState({
            makeChanges: true,
            programChanges: willChange
        })
        console.log(this.state.programChanges)
        console.log({willChange})
    }


    createTable() {
        const { programList } = this.state;
        if (programList.length === 0){
            return <p>Start by adding new credentials</p>
        }
        return <table>
            <tr>
                <th>Program</th>
                <th>Username</th>
                <th>Password</th>
                <th>Actions</th>
            </tr>

            {programList.map((item, i) => (

              <tr>

                <td key={i}>
                  <p>{item.program}</p>
                </td>
                <td key={i}>
                  <p>{item.username}</p>
                </td>
                <td key={i}>
                  <p>{item.password}</p>
                </td>
                <td>
                  <Button className="delete-btn" block bsSize="large" onClick={i => this.handleDeleteRow(item.program)}>
                  </Button>
                  <Button className="change-btn" block bsSize="large" onClick={e => this.handleChangeRow(item.id)}>
                  </Button>
                </td>
            </tr>

            ))}
        </table>
    }



    getToken(){
        return localStorage.getItem('jwt-token');
    };

    componentDidMount() {
        const token = this.getToken();
        //console.log(token)
        if(!token){
            this.props.history.push('/')
        }
        programs(token).then(res => {

            if(!res.err) {
                this.setState({
                    programList: res.programs
                })

                //localStorage.setItem({'programList': res.programs})

            } else{
                console.log(res.err)
            }     
        })        
    }


    render(){
        return (
        <div className="Programs">
            <h1>KEYBANK</h1>
            { this.createTable() }
            <AddCredentialForm/>
        </div>
        )
    }


}

export default ProgramsList;
