import React, { useState } from 'react'
import { Button, FormGroup, FormLabel, FormControl} from "react-bootstrap"
import { login } from './UserFunctions'
import "./Login.css"

export default class LoginForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            masterkey :'',
        };
    }  
    validateForm() {
        return username.length > 0 && masterkey.length > 0;
    }

    handleSubmit(event) {
        event.preventDefault();
        const user = {
            name: username,
            masterkey: masterkey
        }
        login(user).then(res => {
            console.log(user, res)
            if(!res.error) {
                console.log("login success")
            }        
        })
    }
    render(){
        return(
            <div className="Login">
                <form onSubmit={handleSubmit}>
                    <FormGroup controlId="username" bsSize="large">
                        <FormLabel>Username</FormLabel>
                        <FormControl
                            type="username"
                            value={username}
                            onChange={e => setUsername(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey" bsSize="large">
                        <FormLabel>Masterkey</FormLabel>
                        <FormControl
                            type="masterkey"
                            value={masterkey}
                            onChange={e => setMasterkey(e.target.value)}
                        />
                    </FormGroup>
                    <div className="button-group">
                        <Button className="login-btn" block bsSize="large" disabled={!validateForm()} type="submit">
                            Login
                        </Button>
                        or
                        <Link to="/registeration">
                            <Button className="register-btn" block bsSize="large">
                                Register
                            </Button>
                        </Link>
                    </div>
                </form>
            </div>
        );
    }

}
