import axios from 'axios'
import Cookie from 'js-cookie'


export const register = newUser => {
    return axios
        .post('/signup',{
            username: newUser.username,
            masterkey: newUser.masterkey
        })
        .then(res => {
            console.log(res.data)
            return res.data
        })
        .catch(err =>{
            console.log(err)
        })

}

export const login = user => {
    return axios

        .post('/login', {
            username: user.username,
            masterkey: user.masterkey
            })
        .then(res => {
            localStorage.setItem('jwt-token', res.data.token)
            localStorage.setItem('id', res.data.id)
            Cookie.set("token", res.data.token)
            return res.data
        })
        .catch (err => {
            console.log(err)
        })
}

export const credentials = newProgram => {
    const token = localStorage.getItem('jwt-token');
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    return axios

        .post('/add-program', {
            program: newProgram.program,
            username: newProgram.username,
            password: newProgram.password,
            id: newProgram.id
            })
        .then(res => {
            console.log(res.data)
            return res.data
        })
        .catch (err => {
            console.log(err)
        })
}


export const remove = programName => {
    const token = localStorage.getItem('jwt-token');
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    return axios

        .post('/remove-program', {
            program: programName,
            })
        .then(res => {
            console.log(res.data)
            return res.data
        })
        .catch (err => {
            console.log(err)
        })
}


export const programs = async (token) => {
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    try {

        const res = await axios
            .get('/programs', config)
        console.log(res.data)
        return res.data
    }
    catch (err) {
        console.log(err)
    }

}
