import React, { useState } from 'react'
import { Button, FormGroup, FormForm, FormLabel, FormControl} from "react-bootstrap"
import { register } from './UserFunctions'
import "./Signup.css"

export default class RegistrationForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            masterkey :'',
            masterkey2:'',
            password_has_error:false
        };
    }
    validateForm() {
        return username.length > 0 && masterkey.length > 0 && masterkey2.lenght > 0;
    }

    checkMasterkey() {
         if(!this.state.masterkey || this.state.masterkey != this.state.masterkey2) {
            this.setState({masterkey_has_error:true});
        }
        else {
            this.setState({masterkey_has_error:false});
        }
        console.log(masterkey_has_error)
    }
    handleChange(event) {
        this.setState({[event.target.name] : event.target.value });

        if (event.target.name == 'masterkey' || event.target.name == 'masterkey2')
            this.checkPassword();
    }


    handleSubmit(event) {
        event.preventDefault();
        const newUser = {
            name: username,
            masterkey: masterkey
        }
        register(newUser).then(res => {
            console.log(user, res)
            if(!res.error) {

                console.log("registeration success")
            }        
        })
    }
    render(){
        return(
            <div className="Signup">
                <form onSubmit={handleSubmit}>
                    <FormGroup controlId="username" bsSize="large">
                        <FormLabel>Username</FormLabel>
                        <FormControl
                            type="username"
                            value={username}
                            onChange={e => setUsername(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey" bsSize="large">
                        <FormLabel>Masterkey</FormLabel>
                        <FormControl
                            type="masterkey"
                            value={masterkey}
                            onChange={e => setMasterkey(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey2" bsSize="large">
                        <FormLabel>Masterkey again</FormLabel>
                        <FormControl
                            type="masterkey2"
                            value={masterkey2}
                            onChange={e => setMasterkey2(e.target.value)}
                        />
                    </FormGroup>
                    <div className="button-group">
                        <Button className="login-btn" block bsSize="large" disabled={!validateForm()} type="submit">
                            Login
                        </Button>
                        or
                        <Button className="register-btn" block bsSize="large" disabled={!validateForm()} type="submit">
                            Register
                        </Button>
                    </div>
                </form>
            </div>
        );
    }

}
