import React, { useState } from 'react'
import { Button, FormGroup, FormForm, FormLabel, FormControl} from "react-bootstrap"
import { login } from './UserFunctions'

export const LoginForm = () => {
    const [username, setUsername] = useState(''); 
    const [masterkey, setMasterkey] = useState('')

    function validateForm() {
        return username.length > 0 && masterkey.lenght > 0;
    }

    function handleSubmit(event) {
        event.preventDefault();
        const user = {
            username: this.state.username,
            masterkey: this.state.masterkey
        }
        login(user).then(res => {
            if(!res.error)Â {
                this.props.history.push('/profile')
            }
        })
    }

    return(
        <div className="Login">
            <form onSubmit={handleSubmit}>
                <FormGroup controlId="username" bsSize="large">
                    <FormLabel>Username</FormLabel>
                    <FormControl
                        type="username"
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                    />
                </FormGroup>
                <FormGroup controlId="masterkey" bsSize="large">
                    <FormLabel>Masterkey</FormLabel>
                    <FormControl
                        type="masterkey"
                        value={masterkey}
                        onChange={e => setMasterkey(e.target.value)}
                    />
                </FormGroup>
                <Button block bsSize="large" disabled={!validateForm()} type="submit">
                    Login
                </Button>
            </form>
        </div>
    );

}
