import React, { Component } from 'react'
import { Button, FormGroup, FormLabel, FormControl} from "react-bootstrap"
import { programs, credentials } from './UserFunctions'
import "./Programs.css"

class ProgramsList extends Component {
    constructor() {
        super();
        this.state = {
            programList: [],
            username: '',
            password:'',
            password_re:'',
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }

    onChange(e){
        this.setState({[e.target.name]: e.target.value})
    }

    validateForm() {
        if (this.state.username.length > 0 && this.state.password.length > 0 && this.state.password_re.length > 0){ 
            return this.state.password === this.state.password_re
        }          
    }

    onSubmit(e) {
        e.preventDefault();
        const newProgram = {
            username: this.state.username,
            password: this.state.password,
            user_id: localStorage.id
        }
        credentials(newProgram).then(res => {
            if(!res.error) {
                console.log('Credentials added')
            }               
        })
    }

    componentDidMount() {
        console.log('mounted')

        programs().then(res => {

            if(!res.error) {
                this.setState({
                    programList: res.programs
                })
                console.log(this.state.programList)

            } else{
                console.log("error in axios request: apidata")
            }     
        })

    }

    render(){
        const {Â programList } = this.state;
        return (
        <div className="Programs">
            <h1>KEYBANK</h1>
            <table>
                <tr>
                    <th>Username</th>
                    <th>Password</th>
                </tr>
                {programList.map(item =>(
                <tr>
                    <td key={item.program}>
                        {item.program} 
                    </td>
                    <td key={item.program}>
                        {item.password} 
                    </td>  
                </tr>
                ))}
            </table>
            <form noValidate onSubmit={this.onSubmit}>
                <FormGroup controlId="username" bsSize="large">
                    <FormLabel>Username</FormLabel>
                    <FormControl
                        type="username"
                        name="username"
                        value={this.state.username}
                        onChange={this.onChange}
                    />
                </FormGroup>
                <FormGroup controlId="password" bsSize="large">
                    <FormLabel>Password</FormLabel>
                    <FormControl
                        type="password"
                        name="password"
                        value={this.state.password}
                        onChange={this.onChange}
                    />
                </FormGroup>
                <FormGroup controlId="password_re" bsSize="large">
                    <FormLabel>Password again</FormLabel>
                    <FormControl
                        type="password_re"
                        name="password_re"
                        value={this.state.password_re}
                        onChange={this.onChange}
                    />
                </FormGroup>
                <div className="button-group">
                    <Button className="register-btn" block bsSize="large" disabled={!this.validateForm()} type="submit">
                        Add program
                    </Button>
                </div>
            </form>
        </div>
        )
    }
}

export default ProgramsList
