export const programs = async (token) => {
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    try {

        const res = await axios
            .get('/programs', config)
        console.log(res.data)
        return res.data
    }
    catch (err) {
        console.log(err)
    }

}

export const change = programName => {
    const token = localStorage.getItem('jwt-token');
    const userID = localStorage.getItem('id') 
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    return axios

        .post('/change-credentials', {
            program: programName,
            id: userID,
            }, config)
        .then(res => {
            console.log(res.data)
            return res.data
        })
        .catch (err => {
            console.log(err)
        })
}
