import axios from 'axios'

export const auth = checkAccess => {

    return axios

        .post('/check-token', {
            id: checkAccess.id,
            })
        .then(res => {
            console.log(res.data)
            this.props.history.push('/programs')
            return res.data
        })
        .catch (err => {
            console.log(err)
            this.props.history.push('/')
        })
}

export const jwtToken = () => {
    return localStorage.getItem('jwt-token');
}
