import React, { Component } from 'react'
import { Button, FormGroup, FormLabel, FormControl} from "react-bootstrap"
import { programs, credentials, remove } from './UserFunctions'
import { auth } from './AuthFunctions'
import "./Programs.css"

class ProgramsList extends Component {
    constructor() {
        super();
        this.state = {
            programList: [],
            program: '',
            username: '',
            password:'',
            password_re:'',
            jwt_auth: false,

        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
        this.handleDeleteRow = this.handleDeleteRow.bind(this);
    }

    removeRow(e) {
    this.setState({programList: this.state.programList.filter(function(program) { 
        return program !== e.target.value 
    })});
    }

    handleDeleteRow(programName) {
        remove(programName).then(res => {
            if(!res.error) {

                console.log('Credentials deleted')
                this.removeRow()
                this.createTable()
            }               
        })
    }
    createTable() {
        const { programList } = this.state;
        return <table>
            <tr>
                <th>Program</th>
                <th>Username</th>
                <th>Password</th>
                <th>Actions</th>
            </tr>
            {programList.map((item, i) => (
            <tr>
                <td key={i}>
                    {item.program}
                </td>
                <td key={i}>
                    {item.username}
                </td>
                <td key={i}>
                    {item.password}
                </td>
                <td>
                    <Button className="delete-btn" block bsSize="large" onClick={i => this.handleDeleteRow(item.program)}>
                    </Button>
                </td>
            </tr>))}
        </table>
    }

    onChange(e){
        this.setState({[e.target.name]: e.target.value})
    }

    validateForm() {
        if (this.state.username.length > 0 && this.state.password.length > 0 && this.state.password_re.length > 0){ 
            return this.state.password === this.state.password_re
        }          
    }

    onSubmit(e) {
        e.preventDefault();
        const newProgram = {
            program: this.state.program,
            username: this.state.username,
            password: this.state.password,
            id: localStorage.id
        }
        credentials(newProgram).then(res => {
            if(!res.error) {
                var newRow = {
                    program: newProgram.program,
                    username: newProgram.username,
                    password: newProgram.password,
                }
                console.log('Credentials added')
                this.setState({programList: this.state.programList.concat(newRow)})
                this.createTable()
            }               
        })
        this.setState ({
            program: '',
            username: '',
            password:'',
            password_re:'',
        })

    }

    componentDidMount() {
        const gainAccess = localStorage.token
        //auth(gainAccess).then(res =>{
        //    if(!res.error){
        //        this.setState({jwt_auth: true})
        //        console.log('access granted')
        //    }
        //})
        //if(this.jwt_auth){

        programs().then(res => {

            if(!res.error) {
                this.setState({
                    programList: res.programs
                })
                localStorage.setItem({programList: res.programs})
                console.log(localStorage.programList)

            } else{
                console.log("error in axios request: apidata")
            }     
        })
        //}

    }

    render(){

        return (
        <div className="Programs">
            <h1>KEYBANK</h1>
            { this.createTable() }
            <form noValidate onSubmit={this.onSubmit}>
                <FormGroup controlId="program" bsSize="large">
                    <FormLabel>Program</FormLabel>
                    <FormControl
                        type="program"
                        name="program"
                        value={this.state.program}
                        onChange={this.onChange}
                    />
                </FormGroup>
                <FormGroup controlId="username" bsSize="large">
                    <FormLabel>Username</FormLabel>
                    <FormControl
                        type="username"
                        name="username"
                        value={this.state.username}
                        onChange={this.onChange}
                    />
                </FormGroup>
                <FormGroup controlId="password" bsSize="large">
                    <FormLabel>Password</FormLabel>
                    <FormControl
                        type="password"
                        name="password"
                        value={this.state.password}
                        onChange={this.onChange}
                    />
                </FormGroup>
                <FormGroup controlId="password_re" bsSize="large">
                    <FormLabel>Password again</FormLabel>
                    <FormControl
                        type="password"
                        name="password_re"
                        value={this.state.password_re}
                        onChange={this.onChange}
                    />
                </FormGroup>
                <div className="button-group">
                    <Button className="register-btn" block bsSize="large" disabled={!this.validateForm()} type="submit">
                        Add program
                    </Button>
                </div>
            </form>
        </div>
        )
    }


}

export default ProgramsList
