import React, { Component } from 'react'
import { Button, FormGroup, FormLabel, FormControl} from "react-bootstrap"
import { register } from './UserFunctions'
import "./Register.css"

class RegisterForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            masterkey :'',
            masterkey2:'',
            password_has_error:false
        };
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }
    onChange(event){
        this.setState({[event.target.username]: event.target.value})
    }

    validateForm() {
        return this.state.username.length > 0 && this.state.masterkey.length > 0 && this.state.masterkey2.lenght > 0;
    }

    checkMasterkey() {
         if(!this.state.masterkey || this.state.masterkey != this.state.masterkey2) {
            this.setState({masterkey_has_error:true});
        }
        else {
            this.setState({masterkey_has_error:false});
        }
        console.log(this.state.masterkey_has_error)
    }
    handleChange(event) {
        this.setState({[event.target.name] : event.target.value });

        if (event.target.name == 'masterkey' || event.target.name == 'masterkey2')
            this.checkPassword();
    }


    onSubmit(event) {
        event.preventDefault();
        const newUser = {
            username: this.state.username,
            masterkey: this.state.masterkey
        }
        register(newUser).then(res => {
            console.log(newUser, res)
            if(!res.error) {
                this.props.history.push('/login')
                console.log("registeration success")
            }        
        })
    }
    render(){
        return(
            <div className="Register">
                <form noValidate onSubmit={this.onSubmit}>
                    <FormGroup controlId="username" bsSize="large">
                        <FormLabel>Username</FormLabel>
                        <FormControl
                            type="username"
                            value={this.state.username}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey" bsSize="large">
                        <FormLabel>Masterkey</FormLabel>
                        <FormControl
                            type="masterkey"
                            value={this.state.masterkey}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey2" bsSize="large">
                        <FormLabel>Masterkey again</FormLabel>
                        <FormControl
                            type="masterkey2"
                            value={this.state.masterkey2}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <div className="button-group">
                        <Link to="/">
                            <Button className="register-btn" block bsSize="large">
                                Register
                            </Button>
                        </Link>
                        or
                        <Button className="register-btn" block bsSize="large" disabled={!validateForm()} type="submit">
                            Register
                        </Button>
                    </div>
                </form>
            </div>
        );
    }

}
export default RegisterForm
