import axios from 'axios'
import Cookie from 'js-cookie'

export const authorizationCheck = responseData => {
    if (responseData.status === 401){
        props.history.push({
            pathname: '/',
            state: responseData.data.msg
        })
    }
}

export const register = newUser => {
    return axios
        .post('/signup',{
            username: newUser.username,
            masterkey: newUser.masterkey
        })
        .then(res => {
            console.log(res.data)
            return res
        })
        .catch(err =>{
            console.log(err)
        })

}

export const login = user => {
    return axios

        .post('/login', {
            username: user.username,
            masterkey: user.masterkey
            })
        .then(res => {
            localStorage.setItem('jwt-token', res.data.token)
            localStorage.setItem('id', res.data.id)
            Cookie.set("token", res.data.token)
            return res
        })
        .catch (err => {
            console.log(err)
        })
}

export const credentials = credentialSet => {
    const token = localStorage.getItem('jwt-token');
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    const userID = localStorage.getItem('id')
    return axios

        .post('/add-program', {
            program: credentialSet.program,
            username: credentialSet.username,
            password: credentialSet.password,
            index: credentialSet.index,
            id: userID,
            },config)
        .then(res => {
            console.log(res.data)
            console.log(res)
            console.log(res.status)
            return res
        })
        .catch (err => {
            console.log(err)
        })
}


export const remove = removeProgram => {
    const token = localStorage.getItem('jwt-token');
    const userID = localStorage.getItem('id')
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    return axios

        .post('/remove-program', {
            program: removeProgram.program,
            username: removeProgram.username,
            id: userID,
            }, config)
        .then(res => {
            console.log(res.data)
            return res
        })
        .catch (err => {
            console.log(err)
        })
}


export const programs = async (token) => {
    const config = {
        headers: { Authorization: `Bearer ${token}` }
    };
    try {

        const res = await axios
            .get('/programs', config)
        console.log(res.data)
        return res
    }
    catch (err) {
        console.log(err)

    }

}

export const change = credentialSet => {
    console.log(credentialSet)
    const config = {
        headers: { Authorization: `Bearer ${localStorage.getItem('jwt-token')}` }
    };
    return axios

        .post('/change-credentials', {
            program: credentialSet.program,
            username: credentialSet.username,
            password: credentialSet.password,
            index: credentialSet.index,
            user_id: localStorage.getItem('id')
            }, config)
        .then(res => {
            console.log(res.data)
            return res
        })
        .catch (err => {
            console.log(err)
        })
}
