import React, { Component } from 'react'
import { Button, FormGroup, FormLabel, FormControl} from "react-bootstrap"
import { programs, credentials, remove, change } from './UserFunctions'


import "./Programs.css"


class ProgramsList extends Component {
    constructor(props) {
        super(props)
        this.state = {
            programList: [], //store initial list of programs and credentials
            makeChanges: false, //true if user wants to change existing credentials 
            programChanges: [],

            formState: {
                program: '', 
                username: '',
                password:'',
                password_re:'',
                mode: "submit",
            }


        }
        this.getToken = this.getToken.bind(this) //get token from local storage
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
        this.handleDeleteRow = this.handleDeleteRow.bind(this) //handler to delete a row
        this.handleChangeRow = this.handleChangeRow.bind(this)
    }

    resetFormState = () => {
        this.setState({
            formState:{
                program: '',
                username: '',
                password: '',
                password_re: '',
                id: ""
            }
        })
    }

    onChange = e => {
        this.setState({
            formState:{
                ...this.state.formState,

                [e.target.name]: e.target.value
            }
        })
    }

    onSubmit = e => {
        const { programList, formState } = this.state
        e.preventDefault()
        const program = e.target.querySelector("input[name='program']").value      
        const username = e.target.querySelector("input[name='username']").value      
        const password = e.target.querySelector("input[name='password']").value      
        const password_re = e.target.querySelector("input[name='password_re']").value      
        if(formState.mode === "submit"){
            this.setState({
                programList: [
                    ...this.state.programList,
                    {
                        program,
                        username,
                        password,
                        password_re,
                        updating: false,
                        id: this.state.programList[this.state.users.lenght-1].id+1
                    }
                ]
            })
        } else if (formState.mode === "edit"){
            const index = programList.find((item) => item.id === formState.id).id
            programList[index] = {
                program,
                username,
                password,
                updating: false,
                id:programList[index].id
            }
            this.setState({
                programList: [...programList]
            })
        }
        this.resetFormState()
    }

    updateCredentials = key => {
        let { programList } = this.state
        programList[key].updating = true
        this.setState({
            formState: { ...this.state.programList[key], mode: "edit"},
            programList
        })

    }

    deleteCredentials = key => {
        let { programList } = this.state
        programList.splice(key, 1)
        this.setState({
            programList: [...programList]
        })
    }

    handleDeleteRow(programName)Â {
        remove(programName).then(res => {
            if(!res.err) {
                this.removeRow(programName)
                this.createTable()
            }               
        })
    }

    handleChangeRow(programID){
        const willChange = this.state.programList.find(({id}) =>  id === programID)
        this.setState({
            makeChanges: true,
            programChanges: willChange,
            program: '',
            username: '',
            password:'',
            password_re:'',

        }) 
    }


    createForm({ formState }) {
        const Field = ({label ="", name="", value="", onChange}) =>{
            <div className="field">
                <label htmlFor={name}>{label}</label>
                <input type="text" name={name} value={value} onChange={onChange}/>
            </div>
        }

        return (
            <form className="form" onSubmit={this.onSubmit}>
                <fieldset>
                    <Field 
                        name={formState.program}
                        label="program"
                        value={formState.program}
                        onChange={this.onChange}
                    />
                    <Field 
                        name={formState.username}
                        label="username"
                        value={formState.username}
                        onChange={this.onChange}
                    />
                    <Field 
                        name={formState.password}
                        label="password"
                        value={formState.password}
                        onChange={this.onChange}
                    />
                    <Field 
                        name={formState.password_re}
                        label="password again"
                        value={formState.password_re}
                        onChange={this.onChange}
                    />
                </fieldset>
                <div className="button-group">
                    <Button>
                        {formState.mode}
                    </Button>
                </div>
            </form>
        )      
    }

    createTable({ programList=[], updateCredentials, deleteCredentials }) {

        return(
            <table>
                <tr>
                    <th>Program</th>
                    <th>Username</th>
                    <th>Password</th>
                    <th>Actions</th>
                </tr>

                {programList.map((item, key) => (

                    <tr>
                        <td>
                            <p>{item.program}</p>
                        </td>
                        <td>
                            <p>{item.username}</p>
                        </td>
                        <td>
                            <p>{item.password}</p>
                        </td>
                        <td>
                        <Button className="delete-btn" block bsSize="large" onClick={() => updateCredentials(key)}>
                        </Button>
                        <Button className="change-btn" block bsSize="large" onClick={() => deleteCredentials(key)}>
                        </Button>
                        </td>
                    </tr>

                ))}
        </table>
        )}



    validateForm() {
        if('username' in this.state.programChanges){
            return true
        } else{
            if (this.state.username.length > 0 && this.state.password.length > 0 && this.state.password_re.length > 0){ 
                return this.state.password === this.state.password_re
            } 
        }      
    }



    getToken(){
        return localStorage.getItem('jwt-token')
    }

    componentDidMount() {
        const token = this.getToken()
        if(!token){
            this.props.history.push('/')
        }
        programs(token).then(res => {      
            if(!res.err) {
                this.setState({
                    programList: res.programs
                })  
            }
        }).catch(err =>{
            this.props.history.push('/')        
        })
    }


    render(){
        const {programList, formState} = this.state
        return (
            <div className="Programs">
                <h1>KEYBANK</h1>
                <div className="programtable">

                    {this.createTable()}
                    {this.createForm()}


                </div>
                <div className="addform">
                    { this.createForm() }
                </div>
            </div>
        )
    }   
}

export default ProgramsList
