import React, { Component } from 'react'
import { Button, FormGroup, FormLabel, FormControl} from "react-bootstrap"
import { register } from './UserFunctions'
import { Link } from 'react-router-dom'
import "./Register.css"

class RegisterForm extends Component {
    constructor() {
        super();
        this.state = {
            username: '',
            masterkey :'',
            masterkey_re:'',
            password_has_error:false,
        }
        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)
    }
    onChange(e){
        this.setState({[e.target.name]: e.target.value})
    }

    validateForm() {
        return this.state.username.length > 0 && this.state.masterkey.length > 0;
    }
    /*  
    checkMasterkey() {
         if(!this.state.masterkey || this.state.masterkey !== this.state.masterkey_re) {
            this.setState({masterkey_has_error:true});
        }
        else {
            this.setState({masterkey_has_error:false});
        }
        console.log(this.state.masterkey_has_error)
    }

    handleChange(e) {
        this.setState({[e.target.name] : e.target.value });

        if (e.target.name === 'masterkey' || e.target.name === 'masterkey_re')
            this.checkMasterkey();
    }
    */


    onSubmit(e) {
        e.preventDefault();
        const newUser = {
            username: this.state.username,
            masterkey: this.state.masterkey
        }
        register(newUser).then(res => {
            console.log(newUser, res)
            if(!res.error) {
                this.props.history.push('/login')
                console.log("registeration success")
            }        
        })
    }
    render(){
        return(
            <div className="Register">
                <form noValidate onSubmit={this.onSubmit}>
                    <FormGroup controlId="username" bsSize="large">
                        <FormLabel>Username</FormLabel>
                        <FormControl
                            type="username"
                            name="username"
                            value={this.state.username}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey" bsSize="large">
                        <FormLabel>Masterkey</FormLabel>
                        <FormControl
                            type="masterkey"
                            name="masterkey"
                            value={this.state.masterkey}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <FormGroup controlId="masterkey_re" bsSize="large">
                        <FormLabel>Masterkey again</FormLabel>
                        <FormControl
                            type="masterkey_re"
                            name="masterkey_re"
                            value={this.state.masterkey_re}
                            onChange={this.onChange}
                        />
                    </FormGroup>
                    <div className="button-group">
                        <Link to="/">
                            <Button className="login-btn" block bsSize="large">
                                Login
                            </Button>
                        </Link>
                        or
                        <Button className="register-btn" block bsSize="large" disabled={!this.validateForm()} type="submit">
                            Register
                        </Button>
                    </div>
                </form>
            </div>
        );
    }

}
export default RegisterForm
